import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import Footer from './footer'
import { connect } from 'react-redux'
import { createProject } from '../../store/actions/projectActions'

export class SignIn extends Component {
    state = {
        email: '',
        password: '',
        errors: {}
    }
    handleChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })
    }
    handleSubmit = (e) => {
        e.preventDefault();

        const userData = {
            email: this.state.email,
            password: this.state.password
        };
        console.log(userData);

        // console.log(this.state);
        this.props.createProject(this.state)
    }
    render() {
        return (
            <div className="">
                {/* < !--Form starts here-- > */}
                <section class="bk-grey">
                    <div class="container">
                        <div class="row">
                            <div class="col-md-7 login-form">
                                <form onSubmit={this.handleSubmit}>
                                    <div class="main-text">
                                        <h2><i class="fa fa-edit"></i>Login</h2>
                                    </div>
                                    <div class="form-group">
                                        <label htmlFor="exampleInputEmail1">Email address</label>
                                        <input type="email" class="form-control"
                                            aria-describedby="emailHelp" id="email" required onChange={this.handleChange} placeholder="Enter email" />
                                    </div>
                                    <div class="form-group">
                                        <label htmlFor="exampleInputPassword1">Password</label>
                                        <input type="password" class="form-control"
                                            id="password" onChange={this.handleChange} required placeholder="Password" />
                                    </div>
                                    <div class="row">
                                        <div class="form-group form-check check-login col-md-6">
                                            <input type="checkbox" class="form-check-input" id="exampleCheck1" />
                                            <label class="form-check-label" htmlFor="exampleCheck1">Remember me</label>
                                        </div>
                                        <div class="col-md-6 text-right login-txt">
                                            <Link to="">
                                                Forget Password
                                </Link>

                                        </div>
                                    </div>
                                    <div class="login">
                                        <button type="submit" class="btn btn-login btn-block">Login in</button>
                                    </div>
                                    <div class="connect">
                                        <h2>Or connect with</h2>
                                    </div>
                                    <div class="row fg-buttons">
                                        <div class="col-md-6">
                                            <Link to="" class="btn btn-facebook"><i class="fa fa-facebook"></i> Facebook</Link>
                                        </div>
                                        <div class="col-md-6">
                                            <Link to="" class="btn btn-google"><i class="fa fa-google"></i>Google</Link>
                                        </div>
                                    </div>
                                    <div class="connect">
                                        <h2><Link to="/sign-up"> Dont have an account? Register</Link></h2>
                                    </div>
                                </form>
                            </div>
                            <div class="col-md-5 login-img">
                                <img src="../images/vector1.svg" class="img-responsive" alt="" />
                            </div>
                        </div>
                    </div>
                </section>
                <Footer />

                {/* <!-- Form ends here --> */}
            </div>
        )
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        createProject: (project) => dispatch(createProject(project))
    }
}

export default connect(null, mapDispatchToProps)(SignIn)
