import React, { Component } from 'react'
// import Footer from '../../pages/footer'

class CreatePost extends Component {
    state = {
        title: '',
        amount: '',
        location: "",
        status: "",
        category: "",
        stack: "",
        experience: "",
        body: "",
        errors: {}
    }
    componentWillReceiveProps(nextProps) {
        if (nextProps.errors) {
            this.setState({
                errors: nextProps.errors
            });
        }
    }
    componentDidMount() {
        // If logged in and user navigates to Register page, should redirect them to dashboard
        if (this.props.auth.isAuthenticated) {
            this.props.history.push("/dashboard");
        }
    }
    handleChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })
    }
    handleSubmit = (e) => {
        e.preventDefault();

        const newUser = {
            title: this.state.title,
            amount: this.state.amount,
            password: this.state.password,
            password2: this.state.password2
        };

        console.log(newUser);
        // console.log(this.state);
        // this.props.createProject(this.state)

        this.props.registerUser(newUser, this.props.history);
    }

    handleChange
    render() {
        return (
            <div className="container">
                <div className="row">
                    <div className="col-md-12">
                        <h1>Create Posts</h1>

                        {/* {{!-- {{#each errors}} */}
                        <div className="alert alert-danger">
                            {/* {{ message }} */}
                        </div>
                        {/* {{/ each}} --}} */}

                        <form action="/admin/posts/create" method="post" enctype="multipart/form-data">
                            <div class="form-group">
                                <label for="title">Title</label>
                                <input type="text" class="form-control" id="title" placeholder="Enter Title" />
                            </div>
                            <div class="form-group">
                                <label for="title">Amount</label>
                                <select id="amount" class="form-control">
                                    <option value="&#8358;100,000 - &#8358;150,000">&#8358;100,000 - &#8358;150,000</option>
                                    <option value="&#8358;150,000 - &#8358;200,000">&#8358;150,000 - &#8358;200,000</option>
                                    <option value="&#8358;200,000 - &#8358;250,000">&#8358;200,000 - &#8358;250,000</option>
                                    <option value="&#8358;250,000 - &#8358;350,000">&#8358;250,000 - &#8358;350,000</option>
                                    <option value="&#8358;350,000 - &#8358;700,000">&#8358;350,000 - &#8358;700,000</option>
                                </select>
                            </div>
                            <div class="form-group">
                                <label for="title">Location</label>
                                <select id="location" class="form-control">
                                    <option value="Lagos, Nigeria">Lagos, Nigeria</option>
                                    <option value="Ibadan, Nigeria">Ibadan, Nigeria</option>
                                    <option value="Port Harcourt, Nigeria">Port Harcourt, Nigeria</option>
                                    <option value="Abuja, Nigeria">Abuja, Nigeria</option>
                                </select>
                            </div>
                            <div class="form-group">
                                <label for="title">Status</label>
                                <select id="status" class="form-control">
                                    <option value="public">Public</option>
                                    <option value="private">Private</option>
                                    <option value="draft">Draft</option>
                                </select>
                            </div>
                            <div class="form-group">
                                <label for="title">Category</label>
                                <select id="category" class="form-control">
                                    {/* {{#each categories}} */}
                                    {/* <option value="{{id}}">{{ name }}</option> */}
                                    {/* {{/ each}} */}
                                </select>
                            </div>
                            <div class="form-group">
                                <label for="allowComments">Allow Comment</label>
                                <input type="checkbox" class="form-control" id="allowComments" />
                            </div>
                            <div class="row">
                                <div class="col-md-6">
                                    <div class="form-group">
                                        <label for="title">Stack</label>
                                        <input type="text" class="form-control" id="stack" placeholder="Enter Stack" />
                                    </div>
                                </div>
                                <div class="col-md-6">
                                    <div class="form-group">
                                        <label for="title">Experience</label>
                                        <select id="experience" class="form-control">
                                            <option value="Senior">Senior</option>
                                            <option value="Intermediate">Intermediate</option>
                                            <option value="Draft">Junior</option>
                                        </select>
                                    </div>
                                </div>
                            </div>
                            <div class="form-group">
                                <label for="body">Body</label>
                                <textarea cols="30" rows="10" class="form-control" id="title" placeholder="Body"></textarea>
                            </div>
                            <button class="btn btn-primary btn-post-submit" type="submit">Submit</button>
                        </form>
                        {/* <Footer /> */}
                    </div>
                </div>
            </div>
        )
    }
}

export default CreatePost
